    DETERMINISTIC
    COMMENT 'This Function is used to get a sequnce based on the defined sequnces'
BEGIN
   
 			DECLARE LASTINSERTEDID VARCHAR(50);
		
		
    SELECT UM.PublicID INTO LASTINSERTEDID FROM usermaster UM WHERE UM.UserMasterID = (SELECT MAX(USM.UserMasterID) FROM usermaster USM WHERE USM.UserTypeID = UserType_);
        
			
		
				
			-- return the Sequence
			RETURN LASTINSERTEDID ;
				
			
			
    
   
END//
DELIMITER ;

